import React, { Component } from "react";
import "./App.scss";

class App extends Component {
  state = {
    isLoading: false,
    error: null
  };

  componentDidMount() {
    this.setState({ isLoading: true });

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        let lat = position.coords.latitude;
        let lon = position.coords.longitude;
        let url =
          "https://fcc-weather-api.glitch.me/api/current?lat=" +
          lat +
          "&lon=" +
          lon;

        fetch(url)
          .then(resp => {
            if (resp.ok) {
              return resp.json();
            } else {
              throw new Error("something went wrong...");
            }
          })
          //.then(data => console.log(JSON.stringify(data)))
          .then(data => {
            this.setState({
              tempC: data.main.temp,
              tempF: (data.main.temp * 9) / 5 + 32,
              weatherIcon: data.weather[0].main,
              country: data.sys.country,
              city: data.name,
              cOrF: "celsius",
              isLoading: false
            });
            console.log(this.state.weatherIcon);
          })
          .catch(error => this.setState({ error, isLoading: false }));
      });
    } else {
      alert("This browser not supports geolocation");
    }
  }

  changeTemp = () => {
    this.setState({
      cOrF: this.state.cOrF === "celsius" ? "fahrenheit" : "celsius"
    });
    console.log("ola");
  };

  render() {
    const { isLoading } = this.state;
    if (isLoading) {
      return (
        <div id="loading">
          <p className="loading-text">Loading...</p>
          <div className="lds-ripple">
            <div />
            <div />
          </div>
        </div>
      );
    } else {
      return (
        <div>
          <p>
            {this.state.city}, {this.state.country}
          </p>
          {this.state.cOrF === "celsius" && (
            <p onClick={this.changeTemp}>{this.state.tempC}°C</p>
          )}
          {this.state.cOrF === "fahrenheit" && (
            <p onClick={this.changeTemp}>{this.state.tempF}°F</p>
          )}
          <p>{this.state.weatherIcon}</p>

          {this.state.weatherIcon === "Mist" && ( //drizzle
            <div className="icon sun-shower">
              <div id="cloud" />
              <div className="sun">
                <div className="rays" />
              </div>
              <div className="rain" />
            </div>
          )}

          {this.state.weatherIcon === "Clouds" && (
            <div className="icon cloudy">
              <div className="cloud" />
              <div className="cloud" />
            </div>
          )}
          {this.state.weatherIcon === "Rain" && (
            <div className="icon rainy">
              <div className="cloud" />
              <div className="rain" />
            </div>
          )}

          {this.state.weatherIcon === "Snow" && (
            <div>
              <p>ola</p>
              <div className="icon flurries">
                <div className="cloud" />
                <div className="snow">
                  <div className="flake" />
                  <div className="flake" />
                </div>
              </div>
            </div>
          )}

          {this.state.weatherIcon === "Clear" && (
            <div className="icon sunny">
              <div className="sun">
                <div className="rays" />
              </div>
            </div>
          )}

          {this.state.weatherIcon === "Thunderstorm" && (
            <div className="icon thunder-storm">
              <div className="cloud" />
              <div className="lightning">
                <div className="bolt" />
                <div className="bolt" />
              </div>
            </div>
          )}
        </div>
      );
    }
  }
}

export default App;
