import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    isLoading: false,
    error: null
  };

  componentDidMount() {
    this.setState({ isLoading: true });

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        let lat = position.coords.latitude;
        let lon = position.coords.longitude;
        let url =
          "https://fcc-weather-api.glitch.me/api/current?lat=" +
          lat +
          "&lon=" +
          lon;

        fetch(url)
          .then(resp => {
            if (resp.ok) {
              return resp.json();
            } else {
              throw new Error("something went wrong...");
            }
          })
          //.then(data => console.log(JSON.stringify(data)))
          .then(data => {
            this.setState({
              tempC: data.main.temp,
              tempF: (data.main.temp * 9) / 5 + 32,
              main: data.weather[0].main,
              country: data.sys.country,
              city: data.name,
              cOrF: "celsius",
              isLoading: false
            });
          })
          .catch(error => this.setState({ error, isLoading: false }));
      });
    } else {
      alert("This browser not supports geolocation");
    }
  }

  changeTemp = () => {
    this.setState({
      cOrF: this.state.cOrF === "celsius" ? "fahrenheit" : "celsius"
    });
    console.log("ola");
  };

  render() {
    const { isLoading } = this.state;
    if (isLoading) {
      return <p>Loading...</p>;
    } else {
      return (
        <div>
          <p>
            {this.state.city}, {this.state.country}
          </p>
          {this.state.cOrF === "celsius" && (
            <p onClick={this.changeTemp}>{this.state.tempC}°C</p>
          )}
          {this.state.cOrF === "fahrenheit" && (
            <p onClick={this.changeTemp}>{this.state.tempF}°F</p>
          )}
          <p>{this.state.main}</p>

          <div id="icon-mist">
            <div className="front-mist">
              <div className="left-circle" />
              <div className="middle-circle" />
              <div className="right-circle" />
            </div>
            <div className="background-mist">
              <div className="left-circle" />
              <div className="middle-circle" />
              <div className="right-circle" />
            </div>
          </div>
        </div>
      );
    }
  }
}

export default App;
