import React, { Component } from "react";
import "./App.scss";

class App extends Component {
  state = {
    isLoading: false,
    error: null
  };

  componentDidMount() {
    this.setState({ isLoading: true });

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        let lat = position.coords.latitude;
        let lon = position.coords.longitude;
        let url =
          "https://fcc-weather-api.glitch.me/api/current?lat=" +
          lat +
          "&lon=" +
          lon;

        fetch(url)
          .then(resp => {
            if (resp.ok) {
              return resp.json();
            } else {
              throw new Error("something went wrong...");
            }
          })
          //.then(data => console.log(JSON.stringify(data)))
          .then(data => {
            this.setState({
              tempC: data.main.temp,
              tempF: (data.main.temp * 9) / 5 + 32,
              weatherIcon: data.weather[0].main,
              country: data.sys.country,
              city: data.name,
              cOrF: "celsius",
              isLoading: false
            });
          })
          .catch(error => this.setState({ error, isLoading: false }));
      });
    } else {
      alert("This browser not supports geolocation");
    }
  }

  changeTemp = () => {
    this.setState({
      cOrF: this.state.cOrF === "celsius" ? "fahrenheit" : "celsius"
    });
    console.log("ola");
  };

  render() {
    const { isLoading } = this.state;
    if (isLoading) {
      return (
        <div id="loading">
          <p className="loading-text">Loading...</p>
          <div className="lds-ripple">
            <div />
            <div />
          </div>
        </div>
      );
    } else {
      return (
        <div>
          <p>
            {this.state.city}, {this.state.country}
          </p>
          {this.state.cOrF === "celsius" && (
            <p onClick={this.changeTemp}>{this.state.tempC}°C</p>
          )}
          {this.state.cOrF === "fahrenheit" && (
            <p onClick={this.changeTemp}>{this.state.tempF}°F</p>
          )}
          <p>{this.state.weatherIcon}</p>
        </div>
      );

      if (this.state.weatherIcon === "drizzle") {
        return (
          <div class="icon sun-shower">
            <div class="cloud" />
            <div class="sun">
              <div class="rays" />
            </div>
            <div class="rain" />
          </div>
        );
      } else if (this.state.weatherIcon === "clouds") {
        return (
          <div class="icon cloudy">
            <div class="cloud" />
            <div class="cloud" />
          </div>
        );
      } else if (this.state.weatherIcon === "rain") {
        return (
          <div class="icon rainy">
            <div class="cloud" />
            <div class="rain" />
          </div>
        );
      } else if (this.state.weatherIcon === "snow") {
        return (
          <div class="icon flurries">
            <div class="cloud" />
            <div class="snow">
              <div class="flake" />
              <div class="flake" />
            </div>
          </div>
        );
      } else if (this.state.weatherIcon === "clear") {
        return (
          <div class="icon sunny">
            <div class="sun">
              <div class="rays" />
            </div>
          </div>
        );
      } else if (this.state.weatherIcon === "thunderstorm") {
        return (
          <div class="icon thunder-storm">
            <div class="cloud" />
            <div class="lightning">
              <div class="bolt" />
              <div class="bolt" />
            </div>
          </div>
        );
      }
    }
  }
}

export default App;
