import React, { Component } from "react";
import "./App.scss";

class App extends Component {
  state = {
    isLoading: false,
    error: null,
    tempC: 10,
    tempF: 50,
    weatherIcon: "Clear",
    country: "HU",
    city: "Gyor",
    cOrF: "celsius",
    isLoading: false
  };

  componentDidMount() {
    this.setState({
      isLoading: true
    });
    setTimeout(() => {
      this.setState({
        isLoading: false
      });
    }, 1000);
  }
